import React from "react"
import { Modal } from "bootstrap";
import axios from "axios";

class Paket extends React.Component{
    constructor(){
        super()
        this.state = {
            pakets: [
                {
                    id_paket: "121", jenis_paket: "Sandal",
                    harga: "15000"
                },
                {
                    id_paket: "122", jenis_paket: "Sepatu",
                    harga: "300000"
                },
                {
                    id_paket: "123", jenis_paket: "Kaos",
                    harga: "150000"
                }
            ]
        }
    }
    tambahData() {
        //memunculkan mmodal 
        this.modalPaket = new Modal(document.getElementById("modal-paket"))
        this.modalPaket.show()

        //mengosongkan inputannya
        this.setState({
            jenis_paket: "", harga: "", id_paket: Math.random(1, 10000000), 
            action: "tambah"
        })
    }
    simpanData(event) {
        event.preventDefault()
        // mencegah berjalannya aksi default dari form submit

        //menghilangkan modal
        this.modalPaket.hide()

        //cek aksi tambah atau ubah 
        if (this.state.action === "tambah") {
            let endpoint = "http://localhost:8000/paket"
            //menampung data dari pengguna 
            let newPaket = {
                id_paket: this.state.id_paket,
                jenis_paket: this.state.jenis_paket,
                harga: this.state.harga
            }

            // let temp = this.state.pakets
            // temp.push(newPaket)

            // this.setState({ pakets: temp })
            axios.post(endpoint, newPaket)
                .then(response => {
                    window.alert(response.data.message)
                    this.getData()
                })
                .catch(error => console.log(error))

        } else if (this.state.action === "ubah") {
            this.modalPaket.hide()
            let endpoint = "http://localhost:8000/paket" +
            this.state.id_paket

            let newPaket = {
                id_paket: this.state.id_paket,
                jenis_paket: this.state.jenis_paket,
                harga: this.state.harga
            }
            axios.put(endpoint, newPaket)
            .then(response => {
                window.alert(response.data.message)
                this.getData()
            })
            .catch(error => console.log(error))

            // mancari posisis index dari data user berdasarkan id paket pada array 'pakets'
            // let index = this.state.pakets.findIndex(
            //     paket => paket.id_paket === this.state.id_paket
            // )

            // let temp = this.state.pakets
            // temp[index].jenis_paket = this.state.jnis_paket
            // temp[index].harga = this.state.harga

            // this.setState({ pakets: temp })

        }
    }
    ubahData(id_user) {
        this.modalUser = new Modal(document.getElementById("modal-user"))
        this.modalUser.show()

        // mancari posisis index dari data user berdasarkan id user pada array 'users'
        let index = this.state.users.findIndex(
            user => user.id_user === id_user
        )

        this.setState({
            id_user: this.state.users[index].id_user,
            nama: this.state.users[index].nama,
            username: this.state.users[index].username,
            password: this.state.users[index].password,
            action: "ubah"

        })
    }
    hapusData(id_user) {
        if(window.confirm("Apakah anda yakin ingin menghapus data ini?")){
            let endpoint = "http://localhost:8000/users" +
            id_user

            axios.delete(endpoint)
            .then(response => {
                window.alert(response.data.message)
                this.getData()
            })
            .catch(error => console.log(error))
            
            //mecari posisi index dari data yang aan dihapus
            let temp = this.state.users
            let index = temp.findIndex(
                user => user.id_user === id_user
            )

            //menghapus data pada array
            temp.splice(index, 1)

            this.setState({ users: temp })
        }
    }
    getData() {
        let endpoint = "http://localhost:8000/users"
        axios.get(endpoint)
                .then(response => {
                    this.setState({ users: response.data })
                })
                .catch(error => console.log(error))
    }

    componentDidMount() {
        // fungsi ini dijalankan setelah fungsi render berjalan
        this.getData()
    }

    render(){
        return(
            <div className="card">
                <div className="card-header bg-primary">
                    <h4 className="text-white">
                        List Daftar Paket
                    </h4>
                </div>
                <div className="card-body">
                    <ul className="list-group">
                        {this.state.pakets.map(paket => (
                            <li className="list-group-item">
                                <div className="row">
                                    {/* bagian untuk nama */}
                                    <div className="col-lg-5">
                                        <small className="text-info">Jenis Paket</small> <br />
                                        {paket.jenis_paket} 
                                    </div>
                                    
                                    {/* bagian untuk gender */}
                                    <div className="col-lg-3">
                                        <small className="text-info">Harga</small> <br />
                                        {paket.harga}
                                    </div>

                                    <div className="col-lg-2">
                                        <button className="btn btn-warning btn-sm mx-3">Edit</button>
                                        <button className="btn btn-danger btn-sm mx-3">Hapus</button>
                                    </div>

                                    {/* bagian untuk Telepon */}
                                    <div className="col-lg-4">
                                        <small className="text-info">ID Paket</small> <br />
                                        {paket.id_paket}
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="cd_grip gap-2 d-md-flex justify-content-md-end">
                    <button className="btn btn-success mx-3">Tambah Paket</button>
                </div>
            </div>
        )
    }
}

export default Paket
