import React from "react";
import Trial from "./components/Trial";

class App extends React.Component{
  constructor(){
    // constructor -> fungsi yang dijalankan pertama kali saat pemanggilan komponen
    super()
    this.state = {
      label: "Title of Trial Component",
      subtitle: "subtitle of Trial Component",
      color: "success",
      students: [
        {nis: "111", nama: "Rena", kota: "Malang"},
        {nis: "112", nama: "Affan", kota: "Blitar"},
        {nis: "113", nama: "Farrelio", kota: "Malang"},
        {nis: "114", nama: "Fitrotin", kota: "Lumajang"}
      ]
    }
  }
  render(){
    return(
      <div> 
        <Trial title={this.state.label} bgColor={this.state.color}
        subtitle={this.state.subtitle} />

        Text of Label
        <input type="text" className="form-control" 
        value={this.state.label}
        onChange={ev => this.setState({label: ev.target.value})} />

        Text of Subtitle
        <input type="text" className="form-control" 
        value={this.state.subtitle}
        onChange={ev => this.setState({subtitle: ev.target.value})} />

        Color for Trial
        <select className="form-control" value={this.state.color}
        onChange={ev => this.setState({color: ev.target.value})}>
          <option value="danger">Danger</option>
          <option value="warning">Warning</option>
          <option value="success">Success</option>
          <option value="secondary">Secondary</option>
          <option value="info">Info</option>
        </select>

        List of students
        <ul className="list-group">
          <li className="list-group-item">
            <div className="row">
              <div className="col-lg-3">
                <small className="text-primary">NIS</small> <br />
                <h5>111</h5>
              </div>
              <div className="col-lg-4">
                <small className="text-primary">Nama</small> <br />
                <h5>111</h5>
              </div>
              <div className="col-lg-5"></div>
            </div>
          </li>
        </ul>
      </div>
    )
  }
}

export default App;
