import React from "react";
import Trial from "./components/Trial";

class App extends React.Component{
  constructor(){
    // constructor -> fungsi yang dijalankan pertama kali saat pemanggilan komponen
    super()
    this.state = {
      label: "Title of Trial Component",
      subtitle: "subtitle of Trial Component",
      color: "success"
    }
  }
  render(){
    return(
      <div> 
        <Trial title={this.state.label} bgColor={this.state.color}
        subtitle={this.state.subtitle} />

        Text of Label
        <input type="text" className="form-control" 
        value={this.state.label}
        onChange={ev => this.setState({label: ev.target.value})} />

        Text of Subtitle
        <input type="text" className="form-control" 
        value={this.state.subtitle}
        onChange={ev => this.setState({subtitle: ev.target.value})} />

        Color for Trial
        <select className="form-control" value={this.state.color}
        onChange={ev => this.setState({color: ev.target.value})}>
          <option value="danger">Danger</option>
          <option value="warning">Warning</option>
          <option value="success">Success</option>
          <option value="secondart">Danger</option>
        </select>
      </div>
    )
  }
}

export default App;
