import React from "react"
import { Modal } from "bootstrap";

class User extends React.Component{
    constructor(){
        super()
        this.state = {
            users: [
                {
                    id_user: "664", nama: "LuciDuck",
                    username: "lucigtgbgt",
                    password: "okeaja"
                },
                {
                    id_user: "665", nama: "Asmodeus",
                    username: "asmomo",
                    password: "mantapaja"
                },
                {
                    id_user: "666", nama: "Slilit",
                    username: "sliliten",
                    password: "enakaja"
                }
            ]
        }
    }
    tambahData() {
        //memunculkan mmodal 
        this.modalUser = new Modal(document.getElementById("modal-user"))
        this.modalUser.show()

        //mengosongkan inputannya
        this.setState({
            nama: "", username: "", password: "", id_user: Math.random(1, 10000000), 
            action: "tambah"
        })
    }

    simpanData(event) {
        event.preventDefault()
        // mencegah berjalannya aksi default dari form submit

        //menghilangkan modal
        this.modalUser.hide()

        //cek aksi tambah atau ubah 
        if (this.state.action === "tambah") {
            //menampung data dari pengguna 
            let newUser = {
                id_user: this.state.id_member,
                nama: this.state.nama,
                alamat: this.state.alamat,
                telepon: this.state.telepon,
                jenis_kelamin: this.state.jenis_kelamin
            }

            let temp = this.state.members
            temp.push(newMember)

            this.setState({ members: temp })

        } else if (this.state.action === "ubah") {
            this.modalMember.hide()

            // mancari posisis index dari data member berdasarkan id member pada array 'members'
            let index = this.state.members.findIndex(
                member => member.id_member === this.state.id_member
            )

            let temp = this.state.members
            temp[index].nama = this.state.nama
            temp[index].alamat = this.state.alamat
            temp[index].telepon = this.state.telepon
            temp[index].jenis_kelamin = this.state.jenis_kelamin

            this.setState({ members: temp })

        }
    }

    ubahData(id_user) {
        this.modalUser = new Modal(document.getElementById("modal-user"))
        this.modalUser.show()

        // mancari posisis index dari data user berdasarkan id user pada array 'users'
        let index = this.state.users.findIndex(
            user => user.id_user === id_user
        )

        this.setState({
            id_user: this.state.users[index].id_user,
            nama: this.state.users[index].nama,
            alamat: this.state.users[index].alamat,
            jenis_kelamin: this.state.users[index].jenis_kelamin,
            telepon: this.state.users[index].telepon,
            action: "ubah"

        })
    }
    hapusData(id_user) {
        if(window.confirm("Apakah anda yakin ingin menghapus data ini?")){
            //mecari posisi index dari data yang aan dihapus
            let temp = this.state.users
            let index = temp.findIndex(
                user => user.id_user === id_user
            )

            //menghapus data pada array
            temp.splice(index, 1)

            this.setState({ users: temp })
        }
    }

    render(){
        return(
            <div className="card">
                <div className="card-header bg-primary">
                    <h4 className="text-white">
                        List Daftar User
                    </h4>
                </div>
                <div className="card-body">
                    <ul className="list-group">
                        {this.state.users.map(user => (
                            <li className="list-group-item">
                                <div className="row">
                                    {/* bagian untuk nama */}
                                    <div className="col-lg-5">
                                        <small className="text-info">Nama</small> <br />
                                        {user.nama} 
                                    </div>
                                    
                                    {/* bagian untuk gender */}
                                    <div className="col-lg-3">
                                        <small className="text-info">Username</small> <br />
                                        {user.username}
                                    </div>

                                    <div className="col-lg-2">
                                    <button className="btn btn-warning btn-sm mx-3"
                                        onClick={() => this.ubahData(user.id_user)}>
                                        Edit
                                    </button>
                                    <button className="btn btn-danger btn-sm mx-3"
                                        onClick={() => this.hapusData(user.id_user)}>
                                        Hapus
                                    </button>
                                    </div>

                                    {/* bagian untuk Telepon */}
                                    <div className="col-lg-4">
                                        <small className="text-info">ID User</small> <br />
                                        {user.id_user}
                                    </div>

                                    

                                    {/* bagian untuk alamat */}
                                    <div className="col-lg-12">
                                        <small className="text-info">Password</small> <br />
                                        {user.password}
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="cd_grip gap-2 d-md-flex justify-content-md-end">
                    <button className="btn btn-success mx-3">Tambah User</button>
                </div>
            </div>
        )
    }
}

export default User
